import { useEffect, useState } from "react";
import UseEmployerContext from "../../hooks/UseEmployerContext";
import { useLocation } from "react-router-dom";

const JobDetails = () => {
  const { dispatch } = UseEmployerContext();
  const location = useLocation(); // Use useLocation to access passed state
  const { idEmployer, offer } = location.state || {}; // Destructure idEmployer and offer from state

  const [employer, setEmployer] = useState({
    name: "",
    email: "",
    companyName: "",
    img: "",
    jobs: [],
    bio: "",
  });

  console.log("idEmployer", idEmployer); // Debugging to ensure idEmployer is being received

  useEffect(() => {
    if (!idEmployer) {
      console.error("idEmployer is undefined");
      return; // Exit early if idEmployer is not defined
    }

    const fetchEmployer = async () => {
      try {
        const response = await fetch(
          `/API/Employer/single-employer/${idEmployer}`
        );
        if (response.ok) {
          const data = await response.json();
          dispatch({ type: "SET_EMPLOYER", payload: data });
          setEmployer({
            name: data.name,
            companyName: data.companyName,
            email: data.email,
            bio: data.bio,
          });
        } else {
          console.error("Failed to fetch employer data");
        }
      } catch (error) {
        console.error("Error fetching employer data:", error);
      }
    };

    fetchEmployer();
  }, [dispatch, idEmployer]);

  return (
    <div className="job-details-container">
      <div className="employer-section">
        <img
          className="employer-logo"
          src={employer.img}
          alt={`${employer.companyName} logo`}
        />
        <h1 className="employer-name">{employer.companyName}</h1>
        <p className="employer-bio">{employer.bio}</p>
        <p>
          Email: <a href={`mailto:${employer.email}`}>{employer.email}</a>
        </p>
      </div>

      <div className="job-details-section">
        <h2>About this Offer</h2>
        <p>{offer?.description || "No details available"}</p>

        <h3>Job Responsibilities</h3>
        <ul className="job-responsibilities">
          {offer?.responsibilities
            ?.split(",")
            .map((responsibility, index) => (
              <li key={index}>{responsibility.trim()}</li>
            )) || <p>No responsibilities provided</p>}
        </ul>

        <h3>Requirements</h3>
        <ul className="job-requirements">
          {offer?.requirements
            ?.split(",")
            .map((requirement, index) => (
              <li key={index}>{requirement.trim()}</li>
            )) || <p>No requirements provided</p>}
        </ul>

        <h3>Salary</h3>
        <p>{offer?.salary || "Not specified"}</p>

        <h3>Benefits</h3>
        <ul className="job-benefits">
          {offer?.benefits
            ?.split(",")
            .map((benefit, index) => <li key={index}>{benefit.trim()}</li>) || (
            <p>No benefits listed</p>
          )}
        </ul>

        <h3>Hiring Process</h3>
        <p>
          {offer?.hiringProcess ||
            "Details will be shared during the interview"}
        </p>
      </div>
    </div>
  );
};

export default JobDetails;
