import UseOfferContext from "../hooks/UseOfferContext";
import OfferForm from "../components/OfferForm";
import { useState } from "react";

const OfferDetails = ({ offer }) => {
  const { dispatch } = UseOfferContext();
  const [popup, setPopup] = useState(false);
  const [currentOffer, setCurrentOffer] = useState(offer); // Track the offer being edited

  const handleEdit = () => {
    setPopup(true);
  };

  const handleFormSubmit = (updatedOffer) => {
    setCurrentOffer(updatedOffer);
    setPopup(false);

    // Assuming you want to update the offer on the server
    fetch(`/api/offer/update-offer/${offer._id}`, {
      method: "PUT",
      body: JSON.stringify(updatedOffer),
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.message) {
          dispatch({ type: "UPDATE_OFFER", payload: updatedOffer });
        }
      })
      .catch((error) => {
        console.error("Error updating offer:", error);
      });
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`/api/offer/delete-offer/${offer._id}`, {
        method: "DELETE",
      });
      const data = await response.json();
      if (data.message === "Offer job deleted successfully") {
        dispatch({ type: "DELETE_OFFER", payload: { _id: offer._id } });
      } else {
        console.error("Failed to delete offer:", data.message);
      }
    } catch (error) {
      console.error("Error deleting offer:", error);
    }
  };

  return (
    <div className="offer-details">
      <header className="offer-header">
        <h1 className="offer-title">{currentOffer.title}</h1>
        <p className="offer-company">{currentOffer.company}</p>
        <p className="offer-location">{currentOffer.location}</p>
        <p className="offer-publication-date">
          Posted on: {currentOffer.publicationDate}
        </p>
      </header>
      <section className="offer-body">
        <p className="offer-description">{currentOffer.description}</p>
        <h3 className="offer-requirements-title">Requirements</h3>
        <ul className="offer-requirements">
          {currentOffer.requirements.split(",").map((req, index) => (
            <li key={index}>{req.trim()}</li>
          ))}
        </ul>
        <p className="offer-salary">Salary: {currentOffer.salary}</p>
      </section>
      <footer className="offer-actions">
        <button onClick={handleDelete}>Delete</button>
        <button onClick={handleEdit}>Edit</button>
      </footer>

      {popup && (
        <div className="popup">
          <OfferForm initialOffer={currentOffer} onSubmit={handleFormSubmit} />
          <button onClick={() => setPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default OfferDetails;
