import { useEffect, useState } from "react";
import UseEmployerContext from "../../hooks/UseEmployerContext";

const JobDetails = ({ idEmployer }) => {
    const { dispatch } = UseEmployerContext();
  const [employer, setEmployer ] = useState({
    name: "",
    email: "",
    companyName: "",
    img: "",
    jobs: [],
    bio: "",
  });


  useEffect(() => {
    const fetchEmployer = async () => {
      const response = await fetch(
        `/API/Employer/single-employer/${idEmployer}`
      );
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_EMPLOYER", payload: data });
        setEmployer({
          name: data.name,
          companyName: data.companyName,
          email: data.email,
          bio: data.bio,
        });
      }
    };
    fetchEmployer();
  }, [dispatch]);

  return  <div>
  <h1>About this Offer</h1>
  <h2>{employer.name}</h2>
  <h3></h3>
</div>;
};

export default JobDetails;
