import { useEffect, useState } from "react";
import UseJobSeekerContext from "../../hooks/UseJobSeekerContext"; 
import Navbar from "../../components/Navbar";
import "./index.css";

const JobSeekerProfile = () => {
  const idJobSeeker = "66d075786842d41103d96dd1";
  const { dispatch } = UseJobSeekerContext(); // Only extract dispatch
  const [jobseeker, setJobseeker] = useState({
    name: "",
    email: "",
    cv: "",
    phone: "",
    address: "",
    skills: [],
    education: [],
    experience: [],
  });

  useEffect(() => {
    const fetchJobSeeker = async () => {
      try {
        const response = await fetch(
          `/API/jobseeker/single-jobseeker/${idJobSeeker}`
        );
        const data = await response.json();

        if (data) {
          setJobseeker(data);
          dispatch({ type: "SET_JOBSEEKER", payload: data });
        } else {
          console.error("Failed to Set JobSeeker:", data.message);
        }
      } catch (e) {
        console.error("Error fetching jobseeker:", e);
      }
    };
    fetchJobSeeker();
  }, [dispatch]);

  return (
    <>
    <Navbar />
    <div className="jobseeker-profile">
      <div className="profile-header">
        <h1 className="profile-name">{jobseeker.name}</h1>
      </div>
      <div className="profile-details">
        <div className="detail-item">
          <span className="detail-label">CV:</span>
          <span className="detail-value">{jobseeker.cv}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Email:</span>
          <span className="detail-value">{jobseeker.email}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Phone:</span>
          <span className="detail-value">{jobseeker.phone}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Address:</span>
          <span className="detail-value">{jobseeker.address}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Skills:</span>
          <span className="detail-value">{jobseeker.skills.join(", ")}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Education:</span>
          <span className="detail-value">{jobseeker.education.join(", ")}</span>
        </div>
        <div className="detail-item">
          <span className="detail-label">Experience:</span>
          <span className="detail-value">{jobseeker.experience.join(", ")}</span>
        </div>
      </div>
    </div>
  </>
);

};

export default JobSeekerProfile;
