import { BrowserRouter, Routes, Route ,Navigate} from "react-router-dom";
import UseAuthContext from "./hooks/UseAuthContext";
import EmployerHome from "./pages/Employer-home/EmployerHome";
import EmployerProfile from "./pages/Employer-profile/EmployerProfile";
import JobSeekerProfile from "./pages/JobSeeker-profile/JobSeekerProfile";
import JobSeekerHome from "./pages/JobSeeker-home/JobSeekerHome";
import JobDetails from "./pages/JobDetails/JobDetails";
import Login from "./pages/Login/Login";
import Signup from "./pages/Signup/Signup";
import Home from "./pages/Home/Home";
import "./index.css";
function App() {
  const { user } = UseAuthContext();
  return (
    <div className="App">
      <BrowserRouter>
        <div className="pages">
          <Routes>
            <Route path="/" element={ <Home />} />
            <Route path="/Login" element={!user?<Login />: <Navigate to ="/Home"/>} />
            <Route path="/Signup" element={!user?<Signup />: <Navigate to ="/Home"/>} />
            <Route path="/Employer-home" element={user? <EmployerHome/>: <Navigate to ="/Home"/>} />
            <Route path="/Employer-profile" element={user? <EmployerProfile />:<Navigate to ="/Home"/>} />
            <Route path="/JobSeeker-home" element={user?<JobSeekerHome />: <Navigate to ="/Home"/>} />
            <Route path="/JobSeeker-profile" element={user?<JobSeekerProfile />: <Navigate to ="/Home"/>} />
            <Route path="/JobDetails" element={user?<JobDetails />: <Navigate to ="/Home"/>} />
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
