import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import UseAuthContext from "./hooks/UseAuthContext";
import EmployerHome from "./pages/Employer-home/EmployerHome";
import EmployerProfile from "./pages/Employer-profile/EmployerProfile";
import JobSeekerProfile from "./pages/JobSeeker-profile/JobSeekerProfile";
import JobSeekerHome from "./pages/JobSeeker-home/JobSeekerHome";
import JobDetails from "./pages/JobDetails/JobDetails";
import Login from "./pages/Login/Login";
import Signup from "./pages/Signup/Signup";
import Home from "./pages/Home/Home";
import "./index.css";

function App() {
  const { user } = UseAuthContext();

  return (
    <div className="App">
      <BrowserRouter>
        <div className="pages">
          <Routes>
            {/* Public routes */}
            <Route path="/" element={!user ? <Home /> : <Navigate to={user.role === 'employer' ? "/Employer-home" : "/JobSeeker-home"} />} />

            {/* Redirect logged-in users to the appropriate home page */}
            <Route path="/Login" element={!user ? <Login /> : <Navigate to={user.role === 'employer' ? "/Employer-home" : "/JobSeeker-home"} />} />
            <Route path="/Signup" element={!user ? <Signup /> : <Navigate to={user.role === 'employer' ? "/Employer-home" : "/JobSeeker-home"} />} />

            {/* Employer routes */}
            <Route path="/Employer-home" element={user && user.role === 'employer' ? <EmployerHome /> : <Navigate to="/Login" />} />
            <Route path="/Employer-profile" element={user && user.role === 'employer' ? <EmployerProfile /> : <Navigate to="/Login" />} />

            {/* Job Seeker routes */}
            <Route path="/JobSeeker-home" element={user && user.role === 'jobseeker' ? <JobSeekerHome /> : <Navigate to="/Login" />} />
            <Route path="/JobSeeker-profile" element={user && user.role === 'jobseeker' ? <JobSeekerProfile /> : <Navigate to="/Login" />} />

            {/* Job Details accessible to all authenticated users */}
            <Route path="/JobDetails" element={user ? <JobDetails /> : <Navigate to="/Login" />} />
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
