import { useEffect, useState } from "react";

const JobDetails = ({ idEmployer, offer }) => {
  const [employer, setEmployer ] = useState({
    name: "",
    email: "",
    companyName: "",
    img: "",
    jobs: [],
    bio: "",
  });

  useEffect(() => {
    const fetchEmployer = async () => {
      try {
        const response =await fetch(`/api/employer/single-employer/${idEmployer}`);
        const data = await response.json();
        setEmployer(data);
      } catch(e) {
        console.error("Failed to fetch employer:",e);
      }
    };
  }, [setEmployer, idEmployer]);

  return  <div>
  <h1>About this Offer</h1>
  <h2>{employer.bio}</h2>
  <h3>{offer.title}</h3>
</div>;
};

export default JobDetails;
