import { useEffect ,useState} from "react";
import OfferDetails from "../components/OfferDetails";
import OfferForm from "../components/OfferForm";
import UseOfferContext from "../hooks/UseOfferContext";

const Home = () => {
  const { offers, dispatch } = UseOfferContext();

// exercice
  const [count, setCount] = useState(0);
  const add = () => {
    setCount(count + 1);
  };
  const remove = () => {
    if (count > 0) {
      setCount(count - 1);
    }
  };



  const[resources,setResources] = useState('');


  const posts=()=>{
    setResources('post');
  }
  const users=()=>{
  setResources('users');}


  const cmmnts=()=>{
    setResources('cmmnts');}



   const useEffect=useEffect=()=>{
    console.log('render');
   } 

 
// end ex 

  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("/API/offer/all-offer");
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_OFFER", payload: data });
      }
    };
    fetchOffers();
  }, [dispatch]);

  return (
    <div className="home">
      <div className="offer">
        {offers && offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)}
      </div>
      <OfferForm />


      <div>
      <button onClick={add}>+</button>
            <span>{count}</span>
            <button onClick={remove}>-</button>
      </div>



      <div>
        <button onClick={posts}>Posts</button>
        <button onClick={users}> Users</button>
        <button onClick={cmmnts}>Cmmnts</button>
        <div>{resources}</div>
      </div>
    </div>
  );
};

export default Home;
