import { useEffect } from "react";
import OfferDetails from "../components/OfferDetails/index";
import OfferForm from "../components/OfferForm/index";
import UseOfferContext from "../hooks/UseOfferContext";
import EmployerProfile from "./EmployerProfile";


const Home = () => {
  const { offers, dispatch } = UseOfferContext();

 
  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("/API/offer/all-offer");
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_OFFER", payload: data });
      }
    };
    fetchOffers();
  }, [dispatch]);

  return (
    <>
    <div className="home">
      <div className="offer">
        {offers &&
          offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)}
      </div>
      <OfferForm />
    </div>
    <EmployerProfile></EmployerProfile>
    </>
  );
};

export default Home;
