import { useEffect, useState } from "react";

const Home = () => {
  const [offers, setOffers] = useState([]);

  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("/API/offer/all-offer");
      const data = await response.json();

      if (response.ok) {
        setOffers(data);
      }
    };
    fetchOffers();
  }, []);
  return (
    <div className="home">
      <div className="offer">
        {offers &&
          offers.map((offer) => <p key={offer._title}>{offer.location}</p>)}
      </div>
    </div>
  );
};

export default Home;
