  bio: "",
});

const JobDetails = ({ idEmployer, offer }) => {
  useEffect(() => {
    const fetchEmplyer = async () => {
      try {
        fetch(`/api/employer/single-employer/ ${idEmployer}`);
        const data = await response.json();
        setEmplyer(data); 
      } catch {
