import UseOfferContext from "../hooks/UseOfferContext";
import { useState } from "react";

const OfferDetails = ({ offer }) => {
  const { dispatch } = UseOfferContext();
  const [dataOffer, setDataOffer] = useState(offer);
  const [editing, setEditing] = useState(false);

  const handleUpdate = async () => {
    try {
      const response = await fetch(`/api/offer/update-offer/${offer._id}`, {
        method: "PUT",
        body: JSON.stringify(dataOffer),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      console.log(data);
      if (data.message === "Offer job deleted successfully") {
        dispatch({ type: "UPDATE_OFFER", payload: dataOffer });
        setEditing(false); // Exit edit mode on successful update
      }
    } catch (error) {
      console.error("Error updating offer:", error);
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`/api/offer/delete-offer/${offer._id}`, {
        method: "DELETE",
      });
      const data = await response.json();
      console.log("data", data);
      if (data.message === "Offer job deleted successfully") {
        console.log("hello");
        // Dispatch the DELETE_OFFER action with the correct offer ID
        // sabeb
        dispatch({ type: "DELETE_OFFER", payload: { _id: offer._id } });
      } else {
        console.error("Failed to delete offer:", data.message);
      }
    } catch (error) {
      console.error("Error deleting offer:", error);
    }
  };

  return (
    <div className="offer-details">
      <header className="offer-header">
        <h1 className="offer-title">
          {editing ? (
            <input
              type="text"
              value={dataOffer.title}
              onChange={(e) =>setDataOffer({ ...dataOffer, title: e.target.value }) }/>
              ) : ( dataOffer.title  )}
        </h1>
        <p className="offer-company">
          {editing ? (
            <input
              type="text"
              value={dataOffer.company}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, company: e.target.value })
              }
            />
          ) : (
            dataOffer.company
          )}
        </p>
        <p className="offer-location">
          {editing ? (
            <input
              type="text"
              value={dataOffer.location}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, location: e.target.value })
              }
            />
          ) : (
            dataOffer.location
          )}
        </p>
        <p className="offer-publication-date">
          Posted on:{" "}
          {editing ? (
            <input
              type="date"
              value={dataOffer.publicationDate}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, publicationDate: e.target.value })
              }
            />
          ) : (
            dataOffer.publicationDate
          )}
        </p>
      </header>
      <section className="offer-body">
        <p className="offer-description">
          {editing ? (
            <textarea
              value={dataOffer.description}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, description: e.target.value })
              }
            />
          ) : (
            dataOffer.description
          )}
        </p>
        <h3 className="offer-requirements-title">Requirements</h3>
        <ul className="offer-requirements">
          {editing ? (
            <textarea
              value={dataOffer.requirements}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, requirements: e.target.value })
              }
            />
          ) : (
            dataOffer.requirements
              .split(",")
              .map((req, index) => <li key={index}>{req.trim()}</li>)
          )}
        </ul>
        <p className="offer-salary">
          Salary:{" "}
          {editing ? (
            <input
              type="number"
              value={dataOffer.salary}
              onChange={(e) =>
                setDataOffer({ ...dataOffer, salary: e.target.value })
              }
            />
          ) : (
            dataOffer.salary
          )}
        </p>
      </section>
      <footer className="offer-actions">
        {editing ? (
          <>
            <button onClick={handleUpdate}>Save</button>
            <button onClick={() => setEditing(false)}>Cancel</button>
          </>
        ) : (
          <>
            <button onClick={handleDelete}>Delete</button>
            <button onClick={() => setEditing(true)}>Edit</button>
          </>
        )}
      </footer>
    </div>
  );
};

export default OfferDetails;
