import { createContext, useReducer } from "react";
export const OfferContext = createContext();


export const OfferReducer = (state, action) => {
  switch (action.type) {
    case "SET_OFFER":
      return { offer: action.payload };
    case "ADD_OFFER":
      return { offer: [action.payload, ...state.offer] };
    default:
      return state;
  }
};
export const OfferContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(OfferContext, { offer: null });

  return (
    <OfferContext.Provider value={{ ...state, dispatch }}>
      {children}
    </OfferContext.Provider>
  );
};
