import { useEffect ,useState} from "react";
import OfferDetails from "../components/OfferDetails";
import OfferForm from "../components/OfferForm";
import UseOfferContext from "../hooks/UseOfferContext";

const Home = () => {
  const { offers, dispatch } = UseOfferContext();


  const [count, setCount] = useState({number: 0,theme:"Offer"});
  const number =this.number;
  const theme = this.theme;
  const add = () => {
    setCount(number + 1);
    setCount('Offer available');
  };
  const remove = () => {
    if (number > 0) {
      setCount(number - 1);
      setCount('Offer available');
    }
  };

  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("/API/offer/all-offer");
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_OFFER", payload: data });
      }
    };
    fetchOffers();
  }, [dispatch]);

  return (
    <div className="home">
      <div className="offer">
        {offers && offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)}
      </div>
      <OfferForm />


      <div>
      <button onClick={add}>+</button>
            <span>{count}</span>
            <button onClick={remove}>-</button>
      </div>
    </div>
  );
};

export default Home;
