import { useEffect, useState } from "react";
import OfferDetails from "../components/OfferDetails";

const Home = () => {
  const [offers, setOffers] = useState([]);

  const fetchOffers = async () => {
    const response = await fetch("/API/offer/all-offer");
    const data = await response.json();

    if (response.ok) {
      setOffers(data);
    }
  };

  // Fetch offers when the component mounts
  useEffect(() => {
    fetchOffers();
  }, []);

  return (
    <div className="home">
      <div className="offer">
        <button onClick={fetchOffers}>Hunt Job</button>
        {offers &&
          offers.map((offer) => (
            <OfferDetails key={offer._id} offer={offer} />
          ))}
      </div>
    </div>
  );
};

export default Home;
