import { useEffect, useState } from "react";
import OfferDetails from "../components/OfferDetails";

const Home = () => {
  const [offers, setOffers] = useState([]);
  const [loading, setLoading] = useState(false); // State for loading status
  const [error, setError] = useState(null); // State for error handling

  const fetchOffers = async () => {
    setLoading(true); // Start loading
    setError(null); // Clear any previous errors

    try {
      const response = await fetch("/API/offer/all-offer");

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      const data = await response.json();
      setOffers(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false); // End loading
    }
  };

  // Fetch offers when the component mounts
  useEffect(() => {
    fetchOffers();
  }, []);

  return (
    <div className="home">
      <div className="offer">
        <button onClick={fetchOffers} disabled={loading}>
          {loading ? "Loading..." : "Hunt Job"}
        </button>
        {error && <p className="error">{error}</p>} {/* Display error message */}
        {offers && offers.length > 0 ? (
          offers.map((offer) => (
            <OfferDetails key={offer._id} offer={offer} />
          ))
        ) : (
          !loading && <p>No offers available</p> // Message when no offers are available
        )}
      </div>
    </div>
  );
};

export default Home;
