import { useState } from "react";
import { useSignup } from "../../hooks/UseSignup";
import "./signup.css"; // Assuming you're using a dedicated CSS file
import backroundImg from "../../img/backroundImg.jpg";
import NavbarEmployer from "../../components/Navbar/NavbarEmployer";

const Signup = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState(""); // Initially empty
  const { signup, error, isLoading } = useSignup();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (role) {
      await signup(email, password, role);
    } else {
      alert("Please select a role");
    }
  };

  return (
    <>
      <NavbarEmployer />
      <div className="body-auth">
        <img className="background-image-Auth" src={backroundImg} alt="none" />
        <div className="signup-container">
          <form className="signup-form" onSubmit={handleSubmit}>
            <h3>Create Your Account</h3>

            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                id="email"
                type="email"
                placeholder="Enter your email"
                onChange={(e) => setEmail(e.target.value)}
                value={email}
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                placeholder="Enter your password"
                onChange={(e) => setPassword(e.target.value)}
                value={password}
              />
            </div>

            <div className="role-selection">
              <h4>Select Your Role</h4>
              <button type="button" onClick={() => setRole("jobseeker")} className={role === "jobseeker" ? "active" : ""}>
                Jobseeker
              </button>
              <button type="button" onClick={() => setRole("employer")} className={role === "employer" ? "active" : ""}>
                Employer
              </button>
            </div>

            <button type="submit" className="signup-btn" disabled={isLoading}>
              {isLoading ? "Signing up..." : "Sign Up"}
            </button>

            {error && <div className="error-msg">{error}</div>}
          </form>
        </div>
      </div>
    </>
  );
};

export default Signup;
