import React, { useState, useEffect } from "react";
import UseEmployerContext from "../hooks/UseEmployerContext"; // Corrected hook import

function EmployerProfile() {
  const [employer, setEmployer] = useState(null);
  const [error, setError] = useState(null);
  const { dispatch } = UseEmployerContext();

  // Replace with the actual ID or context value
  const idEmployer = "your-employer-id"; // This should come from props, context, or another source

  useEffect(() => {
    const fetchEmployerData = async () => {
      try {
        const response = await fetch(
          `/api/employer/single-employer/${idEmployer}`
        );

        if (!response.ok) {
          throw new Error("Error fetching employer data");
        }

        const data = await response.json();
        setEmployer(data);
      } catch (error) {
        setError(error.message || "An unexpected error occurred");
      }
    };

    fetchEmployerData();
  }, [idEmployer]); // Depend on employerId

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployer((prevEmployer) => ({
      ...prevEmployer,
      [name]: value,
    }));
  };

  const updateEmployer = async () => {
    try {
      const response = await fetch(
        `/api/employer/update-employer/${employer._id}`,
        {
          method: "PATCH",
          body: JSON.stringify(employer),
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (!response.ok) {
        throw new Error("Error updating employer data");
      }

      const data = await response.json();
      dispatch({ type: "UPDATE_EMPLOYER", payload: data });
    } catch (error) {
      setError(
        error.message ||
          "An unexpected error occurred while updating employer data"
      );
    }
  };

  if (error) return <p>Error: {error}</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img src={employer.img} alt="Employer" />
        <h1>{employer.name}</h1>
      </div>
      <div className="profile-details">
        <p>
          <strong>Company:</strong> employer.companyName
        </p>
        <p>
          <strong>Email:</strong> employer.email
        </p>
        <button onClick={() => setIsEditing(true)}>Edit Profile</button>
      </div>
    </div>
  );
}

export default EmployerProfile;
