import React, { useState, useEffect } from "react";


function EmployerProfile() {
  const [employer, setEmployer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchEmployerData = async () => {
      try {
        setLoading(true);
        const response = await fetch(`/API/Employer/all-employer`);

        if (response.status === 404) {
          throw new Error("No such employer found");
        }
        if (response.status === 400) {
          throw new Error("Server error");
        }

        const data = await response.json();
        setEmployer(data);
      } catch (error) {
        setError(error.message || "An unexpected error occurred");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployerData();
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div>
      <h1>Employer Profile</h1>
      {employer ? (
        <div>
          <p>
            <strong>ID:</strong> {employer._id}
          </p>
          <p>
            <strong>Name:</strong> {employer.name}
          </p>
          <p>
            <strong>Email:</strong> {employer.email}
          </p>
        </div>
      ) : (
        <p>No employer data found.</p>
      )}
    </div>
  );
}

export default EmployerProfile;
