import { useEffect, useState } from "react";

const Home = () => {
  const [offers, setOffers] = useState([]);

  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("http://localhost:4000/api/offer");
      const data = await response.json();

      if (response.ok) {
        setOffers(data);
      }
    };
    fetchOffers();
  }, []);
  return (
    <div className="home">
    <div className="offer">
        {offers && offers.map(()=>{
            <p key={offers._id} >{offers.title}</p>
        })}
    </div>
    </div>
  );
};

export default Home;
