import { useEffect } from "react";
import UseEmployerContext from "../../hooks/UseEmployerContext";
import "./EmployerProfile.css"

const EmployerProfile = () => {
  const { employers, dispatch } = UseEmployerContext();
  const idEmployer = "66cb27361832cdc4bc0cef66";

  useEffect(() => {
    const fetchEmployer = async () => {
      const response = await fetch(
        `/API/Employer/single-employer/${idEmployer}`
      );
      const data = await response.json();
      console.log('data', data)

      if (response.ok) {
        dispatch({ type: "SET_EMPLOYER", payload: data });
      }
    };
    fetchEmployer();
  }, [dispatch]);
  const employer = employers.find(emp => emp._id === idEmployer);

  return (
    <div className="employer-profile">
      {/* Profile Header */}
      <div className="profile-header">
        <img
          src={"https://randomuser.me/api/portraits/men/52.jpg"}
        alt="none"
          className="profile-logo"
        />
        <div className="profile-info">
          <h1 className="employer-name">{employer?.name}</h1>
          <p className="employer-company">{employer?.companyName}</p>
        </div>
      </div>

      {/* About Me Section */}
      <div className="profile-about">
        <h2>About Me</h2>
        <p>
          Hi,the founder of {employer?.companyName}, with a
          passion for innovation and excellence in the industry. With extensive
          experience and a strong vision, they have led the company to new
          heights.
        </p>
      </div>

      {/* Company Overview */}
      <div className="profile-company-overview">
        <h2>Company Overview</h2>
        <p>
          {employer?.companyName} is a leading firm specializing in [Industry],
          dedicated to delivering top-notch solutions and services to clients
          worldwide. Our mission is to innovate and provide the highest quality
          products that meet the evolving needs of our customers.
        </p>
      </div>

      {/* Contact Information */}
      <div className="profile-contact-info">
        <h2>Contact Information</h2>
        <p>Email: {employer?.email}</p>
      </div>
    </div>
  );
};

export default EmployerProfile;
