import React, { useState, useEffect } from "react";
import UseOfferContext from "../hooks/UseOfferContext";

function EmployerProfile() {
  const [employer, setEmployer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { dispatch } = UseOfferContext();

  useEffect(() => {
    const fetchEmployerData = async () => {
      try {
        setLoading(true);
        const response = await fetch(`/api/employer/all-employers/`);

        if (response.status === 404) {
          throw new Error("No such employer found");
        }
        if (response.status === 400) {
          throw new Error("Server error");
        }

        const data = await response.json();

        setEmployer(data[0]);
      } catch (error) {
        setError(error.message || "An unexpected error occurred");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployerData();
  }, []);

  const updateEmployer = async () => {
    try {
      const response = await fetch(
        `/api/employer/single-employer/${employer._id}`,
        {
          method: "PATCH",
          body: JSON.stringify(employer),
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      dispatch({type:"UPDATE_EMPLOYER",playload:data});
    } catch (error) {
      setError(
        error.message ||
          "An unexpected error occurred while updating employer data"
      );
    }
  };

  useEffect(() => {
    console.log("employer", employer);
  }, [employer]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img src={employer.img} alt="Employer" />
        <h1>{employer.name}</h1>
      </div>
      <div className="profile-details">
        <p>
          <strong>Company:</strong> {employer.companyName}
        </p>
        <p>
          <strong>Email:</strong> {employer.email}
        </p>
        <button onClick={updateEmployer}>Edit Profil</button>
        
      </div>
    </div>
  );
}

export default EmployerProfile;
