import { useEffect, useState } from "react";

const JobDetails = ({ idEmployer, offer }) => {
  const { employer, setEmployer } = useState({
    name: "",
    email: "",
    companyName: "",
    img: "",
    jobs: [],
    bio: "",
  });

  useEffect(() => {
    const fetchEmplyer = async () => {
      try {
        const response = fetch(`/api/employer/single-employer/ ${idEmployer}`);
        const data = await response.json();
        setEmployer(data);
      } catch {
        console.error("Failed to fetch employer:");
      }
    };
  }, []);

  return <div>About this Offer</div>;
};

export default JobDetails;
