import { useEffect, useState } from "react";
import OfferDetails from "../components/OfferDetails";
import OfferForm from "../components/OfferForm";
import UseOfferContext from "../hooks/UseOfferContext";
import { workout } from "../hooks/workout";

const Home = () => {
  const { offers, dispatch } = UseOfferContext();

  // exercice
  // const [count, setCount] = useState(0);
  // const add = () => {
  //   setCount(count + 1);
  // };
  // const remove = () => {
  //   if (count > 0) {
  //     setCount(count - 1);
  //   }
  // };

  // const[resources,setResources] = useState('');

  // const posts=()=>{
  //   setResources('post');
  // }
  // const users=()=>{
  // setResources('users');}

  // const cmmnts=()=>{
  //   setResources('cmmnts');}

  // end ex

  useEffect(() => {
    const fetchOffers = async () => {
      const response = await fetch("/API/offer/all-offer");
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_OFFER", payload: data });
      }
    };
    fetchOffers();
  }, [dispatch]);

  return (
    <div className="home">
      <div className="offer">
        {offers &&
          offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)}
      </div>
      <OfferForm />
      <workout/>

      
    </div>
  );
};

export default Home;
