import { useEffect } from "react";
import OfferDetails from "../components/OfferDetails/index";
import OfferForm from "../components/OfferForm/index";
import UseEmployerContext from "../hooks/UseEmployerContext";


const Home = () => {
  const { employers, dispatch } = UseEmployerContext();

  const idEmployer = "66c4ab2e7a38eab4113cc004";
  useEffect(() => {
    const fetchEmployer= async () => {
      const response = await fetch( `/api/employer/single-employer/${idEmployer}`);
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_EMPLOYER", payload: data });
      }
    };
    fetchEmployer();
  }, [idEmployer]);

  return (
    <div className="home">
      <div className="offer">
        {offers &&
          offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)}
      </div>
      <OfferForm />
    </div>
  );
};

export default Home;
