import UseOfferContext from "../hooks/UseOfferContext";
import { useState } from "react";

const OfferDetails = ({ offer }) => {
  const { dispatch } = UseOfferContext();
  const [popup, setPopup] = useState(false);
  const [updatedOffer, setUpdatedOffer] = useState(offer);

  const handleInputChanged = (e) => {
    setUpdatedOffer({
      ...updatedOffer,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault(); // Prevent form from submitting normally
    try {
      const response = await fetch(`/api/offer/update-offer/${offer._id}`, {
        method: "PUT",
        body: JSON.stringify(updatedOffer),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await response.json();
      if (data.message) {
        dispatch({ type: "UPDATE_OFFER", payload: updatedOffer });
        setPopup(false); // Close the popup on success
      }
    } catch (error) {
      console.error("Error updating offer:", error);
    }
  };

  return (
    <div className="offer-details">
      {/* Existing Offer Details */}
      <header className="offer-header">
        <h1 className="offer-title">{offer.title}</h1>
        <p className="offer-company">{offer.company}</p>
        <p className="offer-location">{offer.location}</p>
        <p className="offer-publication-date">
          Posted on: {offer.publicationDate}
        </p>
      </header>
      <section className="offer-body">
        <p className="offer-description">{offer.description}</p>
        <h3 className="offer-requirements-title">Requirements</h3>
        <ul className="offer-requirements">
          {offer.requirements.split(",").map((req, index) => (
            <li key={index}>{req.trim()}</li>
          ))}
        </ul>
        <p className="offer-salary">Salary: {offer.salary}</p>
      </section>
      <footer className="offer-actions">
        <button onClick={() => setPopup(true)}>Edit</button>
        <button onClick={handleDelete}>Delete</button>
      </footer>

      {/* Popup for Updating Offer */}
      {popup && (
        <div className="popup">
          <div className="popup-content">
            <h3>Edit Offer</h3>
            <form onSubmit={handleUpdate}>
              <label>Title:</label>
              <input
                type="text"
                name="title"
                value={updatedOffer.title}
                onChange={handleInputChanged}
              />

              <label>Company:</label>
              <input
                type="text"
                name="company"
                value={updatedOffer.company}
                onChange={handleInputChanged}
              />

              <label>Location:</label>
              <input
                type="text"
                name="location"
                value={updatedOffer.location}
                onChange={handleInputChanged}
              />

              <label>Publication Date:</label>
              <input
                type="date"
                name="publicationDate"
                value={updatedOffer.publicationDate}
                onChange={handleInputChanged}
              />

              <label>Salary:</label>
              <input
                type="number"
                name="salary"
                value={updatedOffer.salary}
                onChange={handleInputChanged}
              />

              <label>Requirements:</label>
              <input
                type="text"
                name="requirements"
                value={updatedOffer.requirements}
                onChange={handleInputChanged}
              />

              <label>Description:</label>
              <input
                type="text"
                name="description"
                value={updatedOffer.description}
                onChange={handleInputChanged}
              />

              <button type="submit">Save Changes</button>
              <button type="button" onClick={() => setPopup(false)}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default OfferDetails;
