import { useEffect, useState } from "react";
import UseEmployerContext from "../../hooks/UseEmployerContext";
import "./EmployerProfile.css";
import Navbar from "../../components/Navbar";
import OfferDetails from "../../components/OfferDetails";

const EmployerProfile = () => {
  const { employers, dispatch } = UseEmployerContext();
  const [popup, setPopup] = useState(false);  // Fixed state initialization
  const [formData, setFormData] = useState({
    name: "",
    companyName: "",
    email: "",
    bio: "",
  });

  const idEmployer = "66cb27361832cdc4bc0cef66";

  useEffect(() => {
    const fetchEmployer = async () => {
      const response = await fetch(
        `/API/Employer/single-employer/${idEmployer}`
      );
      const data = await response.json();

      if (response.ok) {
        dispatch({ type: "SET_EMPLOYER", payload: data });
        setFormData({
          name: data.name,
          companyName: data.companyName,
          email: data.email,
          bio: data.bio,
        });
      }
    };
    fetchEmployer();
  }, [dispatch]);

  const handleEdit = () => {
    setPopup(true);
  };

  const handleClose = () => {
    setPopup(false);
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async () => {
    const response = await fetch(
      `/API/Employer/update-employer/${idEmployer}`,
      {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      }
    );
    const data = await response.json();

    if (response.ok) {
      dispatch({ type: "UPDATE_EMPLOYER", payload: data });
      setPopup(false);  // Close the popup after successful update
    }
  };

  return (
    <div className="employer-profile">
      {/* Profile Header */}
      <Navbar />

      <div className="profile-header">
        <img
          src={"https://randomuser.me/api/portraits/men/52.jpg"}
          alt="Profile"
          className="profile-logo"
        />
        <div className="profile-info">
          <h1 className="employer-name">{employers?.name}</h1>
          <p className="employer-company">{employers?.companyName}</p>
          <p className="profile-about">{employers?.email}</p>
        </div>
        <button onClick={handleEdit} className="edit-button">
          Edit Profile
        </button>
      </div>

      {/* About Me Section */}
      <div className="profile-about">
        <h2>About Me</h2>
        <p>{employers?.bio}</p>
      </div>

      {/* Popup Modal for Editing Profile */}
      {popup && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h2>Edit Profile</h2>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Name"
              className="modal-input"
            />
            <input
              type="text"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
              placeholder="Company Name"
              className="modal-input"
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              className="modal-input"
            />
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              placeholder="Bio"
              className="modal-textarea"
            />
            <div className="modal-buttons">
              <button onClick={handleSubmit} className="save-button">
                Save
              </button>
              <button onClick={handleClose} className="cancel-button">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployerProfile;
