import { useEffect, useState } from "react";
import OfferDetails from "../components/OfferDetails/index";
import OfferForm from "../components/OfferForm/index";
import UseEmployerContext from "../hooks/UseEmployerContext";

const Home = () => {
  const { employers, dispatch } = UseEmployerContext();
  const [offers, setOffers] = useState([]);
  const [error, setError] = useState(null);

  const idEmployer = "66cb27361832cdc4bc0cef66"; // Replace with dynamic ID if needed

  useEffect(() => {
    const fetchJobsByEmployer = async () => {
      try {
        const response = await fetch(`/api//all-jobs-byEmployer/${idEmployer}`);
        if (!response.ok) {
          throw new Error("Error fetching jobs");
        }
        const data = await response.json();
        setOffers(data.jobs || []); // Assuming the API returns a 'jobs' array
      } catch (error) {
        setError(error.message || "An unexpected error occurred");
      }
    };

    fetchJobsByEmployer();
  }, [idEmployer]);

  if (error) return <p>Error: {error}</p>;

  return (
    <div className="home">
      <div className="offer">
        {offers.length > 0 ? (
          offers.map((offer) => <OfferDetails key={offer._id} offer={offer} />)
        ) : (
          <p>No jobs available</p>
        )}
      </div>
      <OfferForm />
    </div>
  );
};

export default Home;
