import { useState, useEffect } from "react";
import UseOfferContext from "../hooks/UseOfferContext";

const OfferForm = ({ offer = null, onSubmit }) => {
  const { dispatch } = UseOfferContext();
  const [dataOffer, setDataOffer] = useState({
    title: offer?.title || "",
    company: offer?.company || "",
    location: offer?.location || "",
    publicationDate: offer?.publicationDate || "",
    salary: offer?.salary || "",
    requirements: offer?.requirements || "",
    description: offer?.description || "",
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    if (offer) {
      setDataOffer({ ...offer });
    }
  }, [offer]);

  const validateDataOffer = (dataOffer) => {
    const errors = {};
    if (!dataOffer.title) errors.title = "Title is required";
    if (!dataOffer.company) errors.company = "Company is required";
    if (!dataOffer.location) errors.location = "Location is required";
    if (!dataOffer.publicationDate) errors.publicationDate = "Publication Date is required";
    if (!dataOffer.salary) errors.salary = "Salary is required";
    if (!dataOffer.requirements) errors.requirements = "Requirements are required";
    if (!dataOffer.description) errors.description = "Description is required";
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateDataOffer(dataOffer)) {
      try {
        const response = await fetch(offer ? `/api/offer/update-offer/${offer._id}` : "/API/offer/add-offer", {
          method: offer ? "PUT" : "POST",
          body: JSON.stringify(dataOffer),
          headers: {
            "Content-Type": "application/json",
          },
        });
        const json = await response.json();
        dispatch({ type: offer ? "UPDATE_OFFER" : "ADD_OFFER", payload: json });

        if (!offer) { // Clear the form only if adding a new offer
          setDataOffer({
            title: "",
            company: "",
            location: "",
            publicationDate: "",
            salary: "",
            requirements: "",
            description: "",
          });
        }

        if (onSubmit) onSubmit(); // Call onSubmit callback to handle any additional actions
      } catch (e) {
        console.log("Error:", e);
      }
    }
  };

  return (
    <form className="add_offer" onSubmit={handleSubmit}>
      <h3>{offer ? "Edit Offer" : "Add Offer"}</h3>

      <label>Title:</label>
      <input
        type="text"
        onChange={(e) => {setDataOffer({ ...dataOffer, title: e.target.value }); setErrors({ ...errors, title: "" });}}
        value={dataOffer.title}
        className={errors.title ? "error" : ""}
      />
      {errors.title && <div className="error">{errors.title}</div>}

      <label>Company:</label>
      <input
        type="text"
        onChange={(e) => {setDataOffer({ ...dataOffer, company: e.target.value }); setErrors({ ...errors, company: "" });}}
        value={dataOffer.company}
        className={errors.company ? "error" : ""}
      />
      {errors.company && <div className="error">{errors.company}</div>}

      <label>Location:</label>
      <input
        type="text"
        onChange={(e) => {setDataOffer({ ...dataOffer, location: e.target.value }); setErrors({ ...errors, location: "" });}}
        value={dataOffer.location}
        className={errors.location ? "error" : ""}
      />
      {errors.location && <div className="error">{errors.location}</div>}

      <label>Publication Date:</label>
      <input
        type="date"
        onChange={(e) => {setDataOffer({ ...dataOffer, publicationDate: e.target.value }); setErrors({ ...errors, publicationDate: "" });}}
        value={dataOffer.publicationDate}
        className={errors.publicationDate ? "error" : ""}
      />
      {errors.publicationDate && <div className="error">{errors.publicationDate}</div>}

      <label>Salary:</label>
      <input
        type="number"
        onChange={(e) => {setDataOffer({ ...dataOffer, salary: e.target.value }); setErrors({ ...errors, salary: "" });}}
        value={dataOffer.salary}
        className={errors.salary ? "error" : ""}
      />
      {errors.salary && <div className="error">{errors.salary}</div>}

      <label>Requirements:</label>
      <input
        type="text"
        onChange={(e) => {setDataOffer({ ...dataOffer, requirements: e.target.value }); setErrors({ ...errors, requirements: "" });}}
        value={dataOffer.requirements}
        className={errors.requirements ? "error" : ""}
      />
      {errors.requirements && <div className="error">{errors.requirements}</div>}

      <label>Description:</label>
      <input
        type="text"
        onChange={(e) => {setDataOffer({ ...dataOffer, description: e.target.value }); setErrors({ ...errors, description: "" });}}
        value={dataOffer.description}
        className={errors.description ? "error" : ""}
      />
      {errors.description && <div className="error">{errors.description}</div>}

      <button type="submit">{offer ? "Update Offer" : "Add Offer"}</button>
    </form>
  );
};

export default OfferForm;
