import React, { useState, useEffect } from "react";
import UseOfferContext from "../hooks/UseEmployerContext";

function EmployerProfile() {
  const [employer, setEmployer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const { dispatch } = UseOfferContext();

  useEffect(() => {
    const fetchEmployerData = async () => {
      try {
        setLoading(true);
        const response = await fetch(`/api/employer/single-employer/${employer.id}`);

        if (response.status === 404) {
          throw new Error("No such employer found");
        }
        if (response.status === 400) {
          throw new Error("Server error");
        }

        const data = await response.json();

        setEmployer(data[0]);
      } catch (error) {
        setError(error.message || "An unexpected error occurred");
      } finally {
        setLoading(false);
      }
    };

    fetchEmployerData();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployer((prevEmployer) => ({
      ...prevEmployer,
      [name]: value,
    }));
  };

  const updateEmployer = async () => {
    try {
      const response = await fetch(
        `/api/employer/update-employer/${employer._id}`,
        {
          method: "PATCH",
          body: JSON.stringify(employer),
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      dispatch({ type: "UPDATE_EMPLOYER", payload: data });
      setIsEditing(false);
    } catch (error) {
      setError(
        error.message ||
          "An unexpected error occurred while updating employer data"
      );
    }
  };


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img src={employer.img} alt="Employer" />
        {isEditing ? (
          <input
            type="text"
            name="name"
            value={employer.name}
            onChange={handleChange}
          />
        ) : (
          <h1>{employer.name}</h1>
        )}
      </div>
      <div className="profile-details">
        <p>
          <strong>Company:</strong>{" "}
          {isEditing ? (
            <input
              type="text"
              name="companyName"
              value={employer.companyName}
              onChange={handleChange}
            />
          ) : (
            employer.companyName
          )}
        </p>
        <p>
          <strong>Email:</strong>{" "}
          {isEditing ? (
            <input
              type="email"
              name="email"
              value={employer.email}
              onChange={handleChange}
            />
          ) : (
            employer.email
          )}
        </p>
        {isEditing ? (
          <button onClick={updateEmployer}>Save Changes</button>
        ) : (
          <button onClick={() => setIsEditing(true)}>Edit Profile</button>
        )}
      </div>
    </div>
  );
}

export default EmployerProfile;
