import { useEffect, useState } from "react";
import UseJobSeekerContext from "../../hooks/UseJobSeekerContext"; 
import Navbar from "../../components/Navbar";
import "./index.css";

const JobSeekerProfile = () => {
  const idJobSeeker = "66d075786842d41103d96dd1";
  const { dispatch } = UseJobSeekerContext(); // Only extract dispatch
  const [jobseeker, setJobseeker] = useState({
    name: "",
    email: "",
    cv: "",
    phone: "",
    address: "",
    skills: [],
    education: [],
    experience: [],
  });

  useEffect(() => {
    const fetchJobSeeker = async () => {
      try {
        const response = await fetch(
          `/API/jobseeker/single-jobseeker/${idJobSeeker}`
        );
        const data = await response.json();

        if (data) {
          setJobseeker(data);
          dispatch({ type: "SET_JOBSEEKER", payload: data });
        } else {
          console.error("Failed to Set JobSeeker:", data.message);
        }
      } catch (e) {
        console.error("Error fetching jobseeker:", e);
      }
    };
    fetchJobSeeker();
  }, [dispatch]);

  return (
    <>
    <Navbar />
   
      
      <div className="jobseeker-profile">
        <div className="profile-header">
          <img src="/path/to/profile-picture.jpg" alt="Profile" className="profile-logo" />
          <div className="profile-info">
            <h1 className="jobseeker-name">{jobseeker.name}</h1>
            <p className="jobseeker-email">{jobseeker.email}</p>
            <p className="jobseeker-phone">{jobseeker.phone}</p>
            <p className="jobseeker-address">{jobseeker.address}</p>
          </div>
        </div>
        <div className="profile-about">
          <h2 className="about-me">Skills</h2>
          <p className="about-me-content">{jobseeker.skills.join(", ")}</p>
          <h2 className="about-me">Education</h2>
          <p className="about-me-content">{jobseeker.education.join(", ")}</p>
          <h2 className="about-me">Experience</h2>
          <p className="about-me-content">{jobseeker.experience.join(", ")}</p>
        </div>
      </div>
    </>
);

};

export default JobSeekerProfile;
